    case 103:
      fTemp = mapf(value, 0, 127, 0.0, 3000.0);
      Serial.printf("Attack = %.2f\n", fTemp);
      for (i = 0; i < NUM_VOICES; i++) {
        SynthEngine::voice[i].setADSRAttack(fTemp);
      }
      break;

    case 104:
