      Serial.printf("Decay = %.2f\n", fTemp);
      for (i = 0; i < NUM_VOICES; i++) {
        SynthEngine::voice[i].setADSRDecay(fTemp);
      }
      break;

    case 105:
      fTemp = mapf(value, 0, 127, 0.0, 1.0);
      Serial.printf("Sustain = %.2f\n", fTemp);
      for (i = 0; i < NUM_VOICES; i++) {
        SynthEngine::voice[i].setADSRSustain(fTemp);
      }
      break;

