      Serial.printf("Resonance = %.2f\n", fTemp);
      SynthEngine::Filter.setFiltRes(fTemp);
      break;

    // =================== WAVE SHAPE ======================
    case 110:
    case 111:
    case 116:
    case 117:
      value /= 10;
      switch (value) {
        case 0:
          // nothing for now as it'll be used to disable source below
          break;
        case 1:
        default:
          change = WAVEFORM_SINE;
