        case 3:
          change = WAVEFORM_SAWTOOTH_REVERSE;
          break;
        case 4:
          change = WAVEFORM_SQUARE;
          break;
        case 5:
          change = WAVEFORM_TRIANGLE;
          break;
        case 6:
          change = WAVEFORM_SAMPLE_HOLD;
          break;
        case 7:
          change = WAVEFORM_ARBITRARY;
          break;
        case 8:
          change = WAVEFORM_PULSE;
