        }
      }
      break;

    // ======================== Filter ADSR =======================
    case 22:
      fTemp = mapf(value, 0, 127, 0.0, 3000.0);
      Serial.printf("Filter Attack = %.2f\n", ftemp);
      SynthEngine::Filter.setFiltAttack(fTemp);
