      SynthEngine::Filter.setFiltModulation(n);
      if (n == 0) {
        dumpPatch();
      }
      break;

    // ===================== ARPEGGIATOR ===================
    case 85:
      value /= 20;
      value *= 20;
      Arpeggiator::setArpMode(value);
      break;

    case 86:
      Arpeggiator::setArpOctave(value / 20);
      Serial.printf("Arp Octaves = %d\n", (value / 20) + 1);
      break;

    case 87:
      n = (value >= 64) ? 1 : 0; // 0 /1
      Arpeggiator::setArpOctave(n);
