      break;

    case 90:
      n = (int)mapf(value, 0, 127, 0, 11);
      Arpeggiator::setArpTranspose(n);
      Serial.printf("Arp Transpose = %u\n", n);
      break;

    case 92:
      Arpeggiator::setArpScaleMode(value);
      Serial.printf("Scale = %s\n", scaleModes[value].name);
      break;

    // ===================== CHORUS ====================
    case 91:
      value /= 20;
      value *= 20;
      if (value == 0) {
        n = 0;
      }
      else if (value == 20) {
        n = 2;
      }
      else if (value == 40) {
        n = 4;
      }
      else if (value == 60) {
        n = 6;
      }
      Serial.printf("Chorus = %u\n", n);
      SynthEngine::Filter.setChorusVoices(n);
      updateChorus();
      break;

    // ========================= PANIC =================
    case 123:
      // MIDI PANIC!!
      Arpeggiator::setArpMode(0); // off
      Serial.println("Osc off because Panic");
      oscillatorsOff();
      break;

    default:
      // if unrecognised do nothing
      break;
  }
}

static void onPitchChange(byte channel, int pitch) {
  float change = 0.0;
  Serial.printf("Pitch change: %d\n", pitch);
  if (pitch == 0) {
    osc1PB = 1.0;
    osc2PB = 1.0;
  }
  else {
    // pitch is -8192 to +8192 so convert that to -0.5 to +0.5
    change = (0.5 / 8192) * pitch;
    // so swing from 0.5 to 1.5
    osc1PB = 1.0 + change;
    osc2PB = 1.0 + change;
  }
  Serial.printf("Pitch change: %d so bend = %.05f, osc1PB = %.02f, osc2PB = %.02f\n", pitch, change, osc1PB, osc2PB);
  if (arpNumDown != 0) {
    // something already playing so retrigger with new freq
    oscillatorsOn();
  }
}

