    // ===================== LFO2 ========================
    case 113:
      if (value < 64) {
        fTemp = mapf(value, 0, 64, 0.0, 20.0);        
      }
      else {
        fTemp = mapf(value, 64, 127, 20.0, 200.0);
      }
      Serial.printf("LFO2 freq = %.2fHz\n", fTemp);
      mSynth.LFOs.setLFO2Freq(fTemp);
