        }
      }
      else {
        for (i = 0; i < NUM_VOICES; i++) {
            mSynth.voice[n].setOsc1Mod(Mod_PM);
        }
      }
      break;

    // ======================= OSC2 ============================
    case 119:
        fTemp = mapf(value, 0.0, 127, 0, 12);
        Serial.printf("Osc2 semis = %u\n", fTemp);
        for (i = 0; i < NUM_VOICES; i++) {
            mSynth.voice[n].setOsc2Semis(fTemp);
        }
        break;

    case 26:
        // -2, -1, 0, +1, +2 octave (-24 to +24)
        value = (value > 48) ? 48 : value;
        n = (value / 12) * 12;
        n -= 24;
        for (i = 0; i < NUM_VOICES; i++) {
            mSynth.voice[n].setOsc2Octave(n);
        }
        Serial.printf("Osc1 Octave: %d\n", n);
      break;

    case 27:
        fTemp = mapf(value, 0, 127, 1.0, 0.85);
        Serial.printf("Osc1 detune = %.2f\n", fTemp);
        for (i = 0; i < NUM_VOICES; i++) {
            mSynth.voice[n].setOsc2Detune(fTemp);
        }
        break;

    case 15:
      Serial.printf("Osc2 modulation = %s\n", value == 0 ? "FM" : "PM");
      // 0..63 = FM, 64..127 = PM
      if (value < 64) {
        for (i = 0; i < NUM_VOICES; i++) {
            mSynth.voice[n].setOsc2Mod(Mod_FM);
        }
      }
      else {
        for (i = 0; i < NUM_VOICES; i++) {
            mSynth.voice[n].setOsc2Mod(Mod_PM);
        }
      }
      break;

    // ======================== Filter ADSR =======================
    case 22:
      fTemp = mapf(value, 0, 127, 0.0, 3000.0);
      Serial.printf("Filter Attack = %.2f\n", ftemp);
      mSynth.Filter.setFiltAttack(fTemp);
      break;

    case 24:
      fTemp = mapf(value, 0, 127, 0.0, 3000.0);
      Serial.printf("Filter Decay = %.2f\n", fTemp);
      mSynth.Filter.setFiltDecay(fTemp);
      break;

    case 28:
      fTemp = mapf(value, 0, 127, 0.0, 1.0);
      Serial.printf("Filter Sustain = %.2f\n", fTemp);
      mSynth.Filter.setFiltSustain(fTemp);
      break;

    case 29:
      fTemp = mapf(value, 0, 127, 0.0, 3000.0);
      Serial.printf("Filter Release = %.2f\n", fTemp);
      mSynth.Filter.setFiltRelease(fTemp);
      break;

    // ====================== FILTER ====================
    case 30:
      fTemp = mapf(value, 0, 127, -1.0, 1.0);
      Serial.printf("DC = %.2f\n", fTemp);
      mSynth.Filter.setFiltDC(fTemp);
      break;

    case 31:
      // use amp as a 0/1 switch for filter DC modulation
      n = (value < 64) ? 0 : 1;
      Serial.printf("Modulation = %d\n", n);
      mSynth.Filter.setFiltModulation(n);
      if (n == 0) {
        dumpPatch();
      }
      break;

    // ===================== ARPEGGIATOR ===================
    case 85:
      value /= 20;
      value *= 20;
      mSynth.Arp.setArpMode(value);
      break;

    case 86:
      mSynth.Arp.setArpOctave(value / 20);
      Serial.printf("Arp Octaves = %d\n", (value / 20) + 1);
      break;

    case 87:
      n = (value >= 64) ? 1 : 0; // 0 /1
      mSynth.Arp.setArpOctave(n);
      Serial.printf("Arp Latch = %u\n", n);
      break;

    case 88:
      fTemp = mapf(value, 0, 127, 1200, 50); // not sure of units yet
      mSynth.Arp.setArpPeriod(fTemp);
      Serial.printf("Arp Period = %.2f\n", fTemp);
      break;

    case 89:
      fTemp = mapf(value, 0, 127, 0, 2000);
      mSynth.Arp.setArpDelay(fTemp);
      Serial.printf("Arp Delay = %.2f\n", fTemp);
      break;

    case 90:
      n = (int)mapf(value, 0, 127, 0, 11);
      mSynth.Arp.setArpTranspose(n);
      Serial.printf("Arp Transpose = %u\n", n);
      break;

    case 92:
      mSynth.Arp.setArpScaleMode(value);
      Serial.printf("Scale = %s\n", scaleModes[value].name);
      break;

    // ===================== CHORUS ====================
    case 91:
      value /= 20;
      value *= 20;
      if (value == 0) {
        n = 0;
      }
      else if (value == 20) {
        n = 2;
      }
      else if (value == 40) {
        n = 4;
      }
      else if (value == 60) {
        n = 6;
      }
      Serial.printf("Chorus = %u\n", n);
      mSynth.Filter.setChorusVoices(n);
      updateChorus();
      break;

    // ========================= PANIC =================
    case 123:
      // MIDI PANIC!!
      mSynth.Arp.setArpMode(0); // off
      Serial.println("Osc off because Panic");
      oscillatorsOff();
      break;

    default:
      // if unrecognised do nothing
      break;
  }
}

